import api from "./api-config";

export const getAllPosts = async (board_id) => {
  const resp = await api.get(`/boards/${board_id}/posts`);
  return resp.data;
};

export const getOnePost = async (board_id, id) => {
  const resp = await api.get(`boards/${board_id}/posts/${id}`);
  return resp.data;
};

export const addPost = async (board_id, id) => {
  const resp = await api.post(`boards/${board_id}/posts/${id}`);
  return resp.data;
};

export const updatePost = async (board_id, id) => {
  const resp = await api.put(`boards/${board_id}/posts/${id}`);
  return resp.data;
};

export const destroyPost = async (board_id, id) => {
  const resp = await api.delete(`/boards/${board_id}/posts/${id}`);
  return resp;
};
